namespace IPlusApp.Functionality
{
    public class ExportPdfFunctionality
    {
        #region Fields
        private string patientPath = Path.Combine(Environment.GetFolderPath(Environment.SpecialFolder.ApplicationData), "Patient.txt");

        #endregion
        public void GeneratePdf(ReportModel model)

        {
            var html = "";
            var fileName = Path.Combine(Environment.GetFolderPath(Environment.SpecialFolder.ApplicationData), "IplusReport.html");/// "IplusReport.html"; 
            StreamWriter writer = new StreamWriter(fileName);
            html = GetStartHtml() + GetHeader(model) + GetPatientProfile(model) + GetBreakPage() + GetBreakPage() + GetHeader(model) + GetStatistics(model) + GetBreakPage() + GetBreakPage();
            //html = html + GetBreakPage() + GetBreakPage();
            html = html + GetHeader(model);

            if (model.IsAvaps)
            {
                html = html + GetColumnChartContainer(model.IpapMinimumArray, model.IpapMedianArray, model.IpapPrecentileArray, "IpapContainer", "Ipap");
                html = html + GetColumnChartContainer(model.EpapMinimumArray, model.EpapMedianArray, model.EpapPrecentileArray, "EpapContainer", "Epap");
            }
            if (model.ChAHIAI)
                html = html + GetColumnChartContainer(model.Ahi, "AhiContainer", "Ahi" + "(" + model.AHI + ")");
            if (model.ChLEAK)
                html = html + GetColumnChartContainer(model.LeakMinimumArray, model.LeakMedianArray, model.LeakPrecentileArray, "leakContainer", "leak");
            if (model.ChLEAK)
                html = html + GetColumnChartContainer(model.TidalVolumeMinimumArray, model.TidalVolumeMedianArray, model.TidalVolumePrecentileArray, "TidalVolumeContainer", "Tidal Volume");
            if (model.ChTidalVolume)
                html = html + GetColumnChartContainer(model.TidalVolumeMinimumArray, model.TidalVolumeMedianArray, model.TidalVolumePrecentileArray, "TidalVolumeContainer", "Tidal Volume");
            if (model.ChMinuteVentilation)
                html = html + GetColumnChartContainer(model.MinuteVentilationMinimumArray, model.MinuteVentilationMedianArray, model.MinuteVentilationPrecentileArray, "MinuteVentilationContainer", "Minute Ventilation");
            if (model.ChRespiratoryRate)
                html = html + GetColumnChartContainer(model.RespiratoryRateMinimumArray, model.RespiratoryRateMedianArray, model.RespiratoryRatePrecentileArray, "RespiratoryRateContainer", "Respiratory Rate");
            if (model.ChRespiratoryRate)
                html = html + GetColumnChartContainer(model.ERatioChart, model.IRatioChart, "", "IEContainer", "I:E Ratio");
            if (model.ChUsage)
                html = html + GetColumnChartContainer(model.TotalUsage, "TotalCotainer", "Total Usage");

            html = html + GetEndHtml();

            writer.Write(html);
            writer.Close();
            writer.Dispose();
            System.Diagnostics.Process.Start(Path.GetFullPath(fileName));

        }
        private string GetStartHtml()
        {
            string jqueryPath = Environment.CurrentDirectory + @"\jquery-3.1.1.min.js";
            FileStream stream = new FileStream(jqueryPath, FileMode.Open, FileAccess.Read);
            string jqueryContents;
            using (StreamReader reader = new StreamReader(stream))
            {
                jqueryContents = reader.ReadToEnd();
            }
            string highchartPath = Environment.CurrentDirectory + @"\highcharts.js";
            FileStream highchartstream = new FileStream(highchartPath, FileMode.Open, FileAccess.Read);
            string highchartContents;
            using (StreamReader reader = new StreamReader(highchartstream))
            {
                highchartContents = reader.ReadToEnd();
            }
            string chartfunctionPath = Environment.CurrentDirectory + @"\chartfunctions.js";
            FileStream chartfunctionstream = new FileStream(chartfunctionPath, FileMode.Open, FileAccess.Read);
            string chartfunctionContents;
            using (StreamReader reader = new StreamReader(chartfunctionstream))
            {
                chartfunctionContents = reader.ReadToEnd();
            }
            return "<!DOCTYPE html><html lang='en'>" +
                "<head><meta charset='utf-8'><meta name='viewport' content='width=device-width, initial-scale=1, shrink-to-fit=no'>" +
                $"<script>{jqueryContents}</script>" +
                $"<script>{highchartContents}</script>" +
                $"<script>{chartfunctionContents}</script>" +
                "</head>" +
                "<body style=' max-width: 960px;height: 842px;background: #fff;width: 100%;font-family: sans-serif;font-size: 0.8rem;color: #444444;padding-right: 15px;padding-left: 15px;margin-right: auto;margin-left: auto;'>";
        }
        private string GetEndHtml()
        {
            return "</body></html>";
        }
        private string GetHeader(ReportModel model)
        {
            return $"<div style='width: 100%;float: left;border-bottom: 1px solid #dee2e6'><div style='width: 100%;float: left'><div style='width: 100%;float: left; margin-bottom: .25rem;margin-top: .25rem'><div style='display: flex; align-items: center'> <img  src='data:image/jpeg;base64,' style='width: 170px;float: left;height: 85px;margin-right: 20px;'><div style='border-left: 1px solid #ccc;float: left;width: calc(100% - 150px);padding-left: 15px;'><ul style='float: left;width: calc(100% - 150px);padding-left: 15px;list-style: none'><li style='font-weight: bold; color: #545454;'>Name : <span style='color: #545454;font-weight: normal;'> {model.PatientName}</span></li><li style='font-weight: bold; color: #545454;'>patient ID : <span style='color: #545454;font-weight: normal;'>{model.PatientId}</span></li><li style='font-weight: bold; color: #545454;'>Date of birth : <span style='color: #545454;font-weight: normal;'>{model.DateOfBirth}</span></li><li style='font-weight: bold; color: #545454;'>Report prepared by : <span style='color: #545454;font-weight: normal;'>{model.ReportedBy}</span> on <span style='color: #545454;font-weight: normal;'>{model.Time}</span></li></ul></div></div></div></div> </div>";
            //return $"<div style='width: 100%;float: left;border-bottom: 1px solid #dee2e6'><div style='width: 100%;float: left'><div style='width: 100%;float: left; margin-bottom: .25rem;margin-top: .25rem'><div style='display: flex; align-items: center'> <img src='data:image/jpeg;base64,' style='width: 150px;float: left;'><div style='border-left: 1px solid #ccc;float: left;width: calc(100% - 150px);padding-left: 15px;'><ul style='float: left;width: calc(100% - 150px);padding-left: 15px;list-style: none'><li style='font-weight: bold; color: #545454;'>Name : <span style='color: #545454;font-weight: normal;'> {model.PatientName}</span></li><li style='font-weight: bold; color: #545454;'>patient ID : <span style='color: #545454;font-weight: normal;'>{model.PatientId}</span></li><li style='font-weight: bold; color: #545454;'>Date of birth : <span style='color: #545454;font-weight: normal;'>{model.DateOfBirth}</span></li><li style='font-weight: bold; color: #545454;'>Report prepared by : <span style='color: #545454;font-weight: normal;'>{model.ReportedBy}</span> on <span style='color: #545454;font-weight: normal;'>{model.Time}</span></li></ul></div></div></div></div> </div>";
        }
